31.source:
function reset() {
    this.getPartsBinMetaInfo().addRequiredModule('users.timfelgentreff.babelsberg.constraintinterpreter');
    bbb.unconstrain(this, "hue");
    bbb.unconstrain(this, "luminance")

    // fill with "pixels"
    this.removeAllMorphs();
    this.setExtent(lively.pt(300, 300));

    var pixelCount = parseInt(this.get("NumColors").textString),
        sidel = Math.floor(300 / Math.sqrt(pixelCount)),
        self = this,
        pixelCount = this.getExtent().x / sidel * this.getExtent().y / sidel,
        cassowary = new ClSimplexSolver(),
        lastPx;
    cassowary.setAutosolve(false);

    this.luminance = 0; this.hue = 0.2;

    for (var x = 0; x < this.getExtent().x; x += sidel) {
        for (var y = 0; y < this.getExtent().y; y += sidel) {
            var px = lively.morphic.Morph.makeRectangle(0, 0, sidel, sidel);
            px.luminance = 0; px.lumSum = 0; px.hue = 0; px.hueSum = 0;

            this.constrainPixel(px, cassowary);
            this.sumProperties(px, lastPx, cassowary);

            px.onMouseUp = function (evt) {
                self.openColorChooser(evt, this);
            }.bind(px);
            px.setBorderWidth(0);
            px.setFill(Color.rgb(255,255,255));
            this.addMorph(px);
            px.setPosition(lively.pt(x, y));
            lastPx = px;
        }
    }

    this.constrainColorProperties(lastPx, pixelCount, cassowary);
    this.rerender();
    this.setSliders();
}

