}

58.source:
function sumProperties(px, lastPx, cassowary) {
    if (lastPx) {
        bbb.always({solver: cassowary, ctx: {px: px, lastPx: lastPx}},
        function () {
            return px.lumSum == px.luminance + lastPx.lumSum && px.hueSum == px.hue + lastPx.hueSum
        });
    } else {
        bbb.always({solver: cassowary, ctx: {px: px}},
        function () {
            return px.lumSum == px.luminance && px.hueSum == px.hue;
        });
    }
}

61.source:
function constrainColorProperties(lastPx, pixelCount, cassowary) {
    bbb.always({solver: cassowary,
                ctx: {px: lastPx, pixelCount: pixelCount, self: this, r: bbb.readonly}},
    function () {
        return px.lumSum / pixelCount == self.luminance &&
            px.hueSum / pixelCount == self.hue
    });
}

64.source:
function constrainPixel(px, cassowary) {
    bbb.always({solver: cassowary, ctx: {px: px, self: this}},
    function () {
        var c = px.getFill();
        return c.r >= 0 && c.g >= 0 && c.b >= 0 &&
            c.r <= 1 && c.g <= 1 && c.b <= 1 &&
            px.luminance == 0.2126 * c.r + 0.7152 * c.g + 0.0722 * c.b &&
            px.hue == (c.r + (c.b * 2 + 1) + (c.g * 3 + 2)) / 9.0 &&
            self.luminance * 0.8 <= px.luminance &&
            self.hue * 0.8 <= px.hue
    });
}

