function onEnterPressed(evt) {
    this.getTextString().split("\n").detect(function (line) {
        var unit = line[line.length - 1],
            value = parseFloat(line);
        if (this[unit] != value) {
            try {
                this[unit] = value;
            } catch (e) {
                this.showError(e+"\n\n(Happened trying to set "+unit+" to "+value+")");
            }
            return true;
        }
    }.bind(this));
    evt.stop();
    this.updateLabel(true);
}

