function reset() {
    this.stopStepping();
    ["C", "K", "R", "F", "updater", "updateCelsius"].each(function (unit) {
        bbb.unconstrain(this, unit);
    }.bind(this));
    this.cassowary = new ClSimplexSolver();
    this.C = this.F = this.K = this.R = 0;

    // for example2
    this.updater && (clearInterval(this.updater) || delete this.updater);

    // for example4
    this.deltablue = new DBPlanner();
    this.warn && this.warn.remove();
    this.warn = new lively.morphic.HtmlWrapperMorph(this.getExtent());
    this.warn.asJQuery().html('<div id="tooHotWarning">&nbsp;&nbsp;&nbsp;&nbsp;</div>');
    this.warn.applyStyle({fill: Color.rgba(0,0,0,0), clipMode: 'auto'});
    this.warn.setBounds(this.getBounds().translatedBy(pt(0, this.getBounds().height)));

    // for example5
    this.callback && (this.callback() || delete this.callback)
    this.slider && this.slider.remove();
    this.slider = new lively.morphic.Slider(lively.rect(0,this.getBounds().bottom(),155,26));
    this.slider.onMouseDown = (function (evt) { return false; })


    this.startStepping(500, "updateLabel");
}

