function addConstraints() {
    var f = this.get("Fahrenheit"), c = this.get("Celsius"), ft = this.get("FLabel"), ct = this.get("CLabel"), cassowary = new ClSimplexSolver(), deltablue = new DBPlanner(), db2 = new DBPlanner();
    // db2.weight -= 1;
    bbb.always({
        solver: cassowary,
        ctx: {
            ClSimplexSolver: ClSimplexSolver,
            DBPlanner: DBPlanner,
            cassowary: cassowary,
            f: f,
            c: c,
            deltablue: deltablue,
            ft: ft,
            Math: Math,
            parseFloat: parseFloat,
            db2: db2,
            ct: ct,
            _$_self: this.doitContext || this
        }
    }, function() {
        return f.getValue() * 100 - 32 == c.getValue() * 100 * 1.8 && f.getValue() >= 0 && c.getValue() >= 0 && f.getValue() <= 1 && c.getValue() <= 1;;
    });
