function reset() {
    var f = this.get('Fahrenheit'),
        c = this.get('Celsius'),
        ft = this.get('FLabel'),
        ct = this.get('CLabel');

    (function () {
        return (f.getValue() * 100) - 32 == (c.getValue() * 100) * 1.8;
    }).shouldBeTrue({f: f, c: c});

    (function () {
        return f.getValue() >= 0 && c.getValue() >= 0;
    }).shouldBeTrue({f: f, c: c});

    (function () {
        return f.getValue() <= 100 && c.getValue() <= 100;
    }).shouldBeTrue({f: f, c: c});

    (function () {
        return f.getValue() * 100 == ft.getTextString();
    }).shouldBeSatisfiedWith(function () {
        ft.getTextString().formula([f.getValue()], function (v) { return v * 100 + ""; });
    }, {ft: ft, f: f});
    (function () {
        return c.getValue() * 100 == ct.getTextString();
    }).shouldBeSatisfiedWith(function () {
        ct.getTextString().formula([c.getValue()], function (v) { return v * 100 + ""; });
    }, {ct: ct, c: c});
}
